<?phpclass Blog_forum_news_Controller extends Controller {    private $pageTpl = '/templates/page_all.tpl';    public function __construct() {        $this->model = new Blog_forum_news_Model();        $this->view = new View();    }    public function index() {        $this->controller();        $this->pageData['pageall'] = $this->echo_page_content();        $this->pageData['pagealltitles'] = $this->echo_page_titles();        $this->pageData['slash'] = "../";        $this->view->render($this->pageTpl, $this->pageData);    }    public function echo_page_content() { //Ф-я для вывода блога        global $env;        $someresult = "";        $routetitle =$env['route'];        $all = $this->model->getpagetitles();        $count = count($all);        for($i = 0; $i < $count; $i++){            $subcategory=$all[$i]["name"];            $subcategory_translit = $this->translit($subcategory);            $subcategoryes = <<<"EOT"                <li class="py-2">                    <a href="/$routetitle/$subcategory_translit">$subcategory</a>                </li>EOT;            $someresult = $someresult.$subcategoryes;        }        return $someresult;    }    public function echo_page_titles(){ //Ф-я для вывода блога    global $env;        $someresultall = "";        $alltitles = $this->model->getpagetitles();            $category = ucfirst($env['route']);            $titles = <<<"EOT"                <h4>$category</h4>EOT;            $pageallecho = <<<"EOT"                </div>                <div class="col-4">                  <ul class="nav align-items-center justify-content-center">                    <li class="nav-item">                      <button type="button" class="btn-add rounded-circle bg-secondary">                        <i class="bi bi-plus text-white"></i>                      </button>                    </li>                    <li class="nav-item"><a href="#" class="nav-link">FORUM</a></li>                    <li class="nav-item"><a href="#" class="nav-link">BLOG</a></li>                  </ul>                </div>              </div>              <div class="row px-4 py-2">                <div class="col-8 px-0">                  <nav>                    <ul class="category-list p-0">EOT;            $someresultall = $titles.$pageallecho.$this->echo_page_content();        return $someresultall;    }}