<?phpclass Blog_forum_news_Controller extends Controller {    private $pageTpl = '/templates/page_all.tpl';    public function __construct() {        $this->model = new Blog_forum_news_Model();        $this->view = new View();    }    public function index() {        global $env;        //if(isset($_SESSION['user_id'])&&$_SESSION['user_id'] !=''){        if(isset($_COOKIE['user_id'])&&$_COOKIE['user_id'] !=''){                $Fn = str_split($_COOKIE['first-name']);                $Ln = str_split($_COOKIE['last-name']);                $resultFnLnarr = $Fn['0'].$Ln['0'];                if($_COOKIE['status'] == 'admin'){                    $adminPanel =                        '<form method="post">							<input type="submit" name="act" class="btn btn-primary btn-lg" value="Admin Panel">						</form>';                }                else{                    $adminPanel = '';                }            }            else{                $resultFnLnarr = 'No';                $adminPanel = '';                $_COOKIE['status'] = 'user';                $_COOKIE['user_id'] = '';            }        }        $this->pageData['pageall'] = $this->echo_page_content();        $this->pageData['pagealltitles'] = $this->echo_page_titles();        $this->pageData['topmenu'] = $this->echo_topmenu();//        $this->pageData['active'] = $active;        $this->pageData['burger'] = $this->echo_burger();        $this->pageData['panel'] = $adminPanel;        $this->pageData['check'] = $resultFnLnarr;        $this->pageData['title'] = "Forum-blog";        $this->pageData['slash'] = "../";        $this->view->render($this->pageTpl, $this->pageData);    }    public function echo_page_content() { //Ф-я для вывода блога        global $env;        $someresult = "";        $routetitle =$env['route'];        $all = $this->model->getpagetitles();        $count = count($all);        for($i = 0; $i < $count; $i++){            $subcategory=$all[$i]["name"];            $subcategoryes = <<<"EOT"                <li class="py-2">                    <a href="/$routetitle/$subcategory">$subcategory</a>                </li>EOT;            $someresult = $someresult.$subcategoryes;        }        return $someresult;    }    public function echo_page_titles(){ //Ф-я для вывода блога    global $env;        $someresultall = "";        $alltitles = $this->model->getpagetitles();            $category = ucfirst($env['route']);            $titles = <<<"EOT"                <h4>$category</h4>EOT;            $pageallecho = <<<"EOT"                </div>                <div class="col-4">                  <ul class="nav align-items-center justify-content-center">                    <li class="nav-item">                      <button type="button" class="btn-add rounded-circle bg-secondary">                        <i class="bi bi-plus text-white"></i>                      </button>                    </li>                    <li class="nav-item"><a href="#" class="nav-link">FORUM</a></li>                    <li class="nav-item"><a href="#" class="nav-link">BLOG</a></li>                  </ul>                </div>              </div>              <div class="row px-4 py-2">                <div class="col-8 px-0">                  <nav>                    <ul class="category-list p-0">EOT;            $someresultall = $titles.$pageallecho.$this->echo_page_content();        return $someresultall;    }}