<?phpclass AllModel extends Model {    public function getpagealltitles(){        global $env;        $resultpage = array();        $sql = "SELECT DISTINCT `Category` FROM `blog` UNION SELECT `Category` FROM `forum`";        $ppagealltitles = $this->db->prepare($sql);        $ppagealltitles->execute();        while($pagealltitles=$ppagealltitles->fetch(PDO::FETCH_ASSOC)){            array_push($resultpage,$pagealltitles);        }        return($resultpage);    }    public function getpageall(){        global $env;        $resultpageall = array();        $title = $env['all_title'];        $sql = "SELECT `Description` FROM `forum_category` JOIN `forum` ON `forum`.`id` = `forum_category`.`Category` WHERE `forum`.`Category` = :title UNION SELECT `Description` FROM `blog_category` JOIN `blog` ON `blog`.`id` = `blog_category`.`Category` WHERE `blog`.`Category` = :title";        $ppageall = $this->db->prepare($sql);        $ppageall->bindValue(":title", $title, PDO::PARAM_STR);        $ppageall->execute();        while($respageall=$ppageall->fetch(PDO::FETCH_ASSOC)){            array_push($resultpageall,$respageall);        }        return($resultpageall);    }}