<?phpclass Blog_forum_news_Model extends Model {    public function getpagetitles(){        global $env;        $resultpageall = array();        $sql = "SELECT `name` FROM `".$env['route']."`";        $ppageall = $this->db->prepare($sql);        $ppageall->execute();        while($respageall=$ppageall->fetch(PDO::FETCH_ASSOC)){            array_push($resultpageall,$respageall);        }        return($resultpageall);    }}