<?phpclass Categories_allModel extends Model {    public function get_all_categories(){        $resultpage = array();        $sql = "SELECT DISTINCT Category FROM (SELECT Category FROM blog UNION ALL SELECT Category FROM forum) AS combined";        $ppagealltitles = $this->db->prepare($sql);        $ppagealltitles->execute();        while($pagealltitles=$ppagealltitles->fetch(PDO::FETCH_ASSOC)){            array_push($resultpage,$pagealltitles);        }        return($resultpage);    }    public function get_all_subcategories($title){        global $env;        $resultpageall = array();        $sql = "SELECT `forum_category`.`Description`, `forum`.`structure`, `forum`.`Category`                    FROM `forum_category`                        JOIN `forum`                            ON `forum`.`id` = `forum_category`.`Category`                                WHERE `forum`.`Category` = :title                                    UNION SELECT `blog_category`.`Description`, `blog`.`structure`, `blog`.`Category`                                        FROM `blog_category`                                            JOIN `blog`                                                ON `blog`.`id` = `blog_category`.`Category`                                                    WHERE `blog`.`Category` = :title";        $ppageall = $this->db->prepare($sql);        $ppageall->bindValue(":title", $title, PDO::PARAM_STR);        $ppageall->execute();        while($respageall=$ppageall->fetch(PDO::FETCH_ASSOC)){            array_push($resultpageall,$respageall);        }        return($resultpageall);    }}